mapper.select:
SELECT COUNT(*)
FROM TB_BOARD
WHERE BTYPE = #{mode}

	AND STUDENT_ID = #{stdId}

SELECT A.* , ROWNUM FROM ( SELECT B.BOARD_ID , B.BTITLE , B.BDATE , B.BCONTENTS , B.BCOUNT , B.BTYPE , S.STUDENT_NAME , B.STUDENT_ID FROM TB_BOARD B JOIN TB_STUDENT S ON B.STUDENT_ID = S.STUDENT_ID WHERE BTYPE = #{mode}
AND B.STUDENT_ID = #{stdId}
ORDER BY BDATE DESC ) A

mapper.update:
UPDATE TB_BOARD 
	SET BANSWER = #{bAnswer}
WHERE BOARD_ID = #{boardId}
