mapper.select:
SELECT a.CLASS_SEQ, a.CLASS_NAME, c.DEPARTMENT_NAME AS DEPARTMENT_CODE, d.NAME AS PROFESSOR_NAME, subquery1.total, a.ROOM, a.CLASS_TYPE, a.GRADE_SIZE, a.CLASS_LEVEL FROM TB_CLASS a, (SELECT CLASS_SEQ, LISTAGG(CONCAT(DAY, HOUR), '/') WITHIN GROUP (ORDER BY CLASS_TIME_SEQ ) AS total FROM TB_CLASS_TIME b GROUP BY CLASS_SEQ) subquery1, TB_DEPARTMENT c, TB_PROFESSOR d WHERE subquery1.CLASS_SEQ = a.CLASS_SEQ AND a.DEPARTMENT_CODE = c.DEPARTMENT_CODE AND a.PROFESSOR_ID = d.PROFESSOR_ID AND a.CLASS_APPROVE = 'Y'
AND a.CLASS_TYPE = #{classType}
AND a.CLASS_NAME LIKE '%'|| #{inputClassName} || '%'
AND a.CLASS_LEVEL = #{classLevel}
AND c.COLLEGE_CODE = #{collegeCode}
AND c.DEPARTMENT_NAME=#{departmentName}
AND a.CLASS_NAME = #{className}

SELECT DEPARTMENT_NAME AS DEPARTMENT_CODE
FROM TB_DEPARTMENT
WHERE COLLEGE_CODE = #{collegeCode}

SELECT CONCAT(DAY,HOUR) AS DAY_HOUR
FROM TB_CLASS_TIME
WHERE CLASS_SEQ=#{classSeq}

SELECT CLASS_SEQ
FROM TB_CLASS
WHERE CLASS_CODE=#{classCode}
